CREATE TABLE `ordermgmt` (`order_id` int(11) NOT NULL COMMENT 'Identifying row',`ordered_by` int(11) NOT NULL COMMENT 'The userid of the person that generated this requisition',`company` int(11) NOT NULL COMMENT 'The id of the company related to the order',`main_project` int(11) NULL DEFAULT NULL COMMENT 'The id of the project this order belongs to',`notes` text CHARACTER SET latin1 COLLATE latin1_swedish_ci NULL,`date_created` timestamp NOT NULL DEFAULT 'CURRENT_TIMESTAMP',PRIMARY KEY (`order_id`) );CREATE TABLE `ordermgmt_components` (`component_id` int(11) NOT NULL,`component_price` float(11,2) NOT NULL,`component_amount` int(11) NOT NULL,`component_description` tinytext CHARACTER SET latin1 COLLATE latin1_swedish_ci NOT NULL,`project` int(11) NULL DEFAULT NULL,`order_id` int(11) NOT NULL,PRIMARY KEY (`component_id`) ,INDEX `requisition_component_fk` (`order_id`));CREATE TABLE `ordermgmt_default_components` (`component_id` int(11) NOT NULL,`catalog_number` varchar(30) CHARACTER SET latin1 COLLATE latin1_swedish_ci NOT NULL,`brand` varchar(30) CHARACTER SET latin1 COLLATE latin1_swedish_ci NULL DEFAULT NULL,`supplier` varchar(50) CHARACTER SET latin1 COLLATE latin1_swedish_ci NULL DEFAULT NULL,`discount` float NULL DEFAULT NULL,`vendor_price` float NOT NULL,`exchange_rate` float NOT NULL,`local_price` float NOT NULL,`quote_date` timestamp NOT NULL DEFAULT 'CURRENT_TIMESTAMP',`description` mediumtext CHARACTER SET latin1 COLLATE latin1_swedish_ci NOT NULL,`notes` mediumtext CHARACTER SET latin1 COLLATE latin1_swedish_ci NOT NULL,PRIMARY KEY (`component_id`) );CREATE TABLE `ordermgmt_files` (`file_id` int(11) NOT NULL COMMENT 'Forign key to existing file storage structure, used to identify file.',`order_id` int(11) NOT NULL COMMENT 'Forign key used to reference the requisitions table to assosiate a file with a requisition.',PRIMARY KEY (`file_id`, `order_id`) ,INDEX `file_requisition_fk` (`order_id`));CREATE TABLE `ordermgmt_status` (`order_status_id` int(11) NOT NULL,`order_id` int(11) NOT NULL,`user_id` int(11) NOT NULL,`status_id` int(11) NOT NULL,`date_changed` timestamp NOT NULL DEFAULT 'CURRENT_TIMESTAMP',`comments` mediumtext CHARACTER SET latin1 COLLATE latin1_swedish_ci NULL,PRIMARY KEY (`order_status_id`) ,INDEX `req_status_fk` (`order_id`),INDEX `status_statusinfo_fk` (`status_id`));CREATE TABLE `ordermgmt_status_info` (`order_status_info_id` int(11) NOT NULL,`status_title` varchar(45) CHARACTER SET latin1 COLLATE latin1_swedish_ci NOT NULL,`status_information` tinytext CHARACTER SET latin1 COLLATE latin1_swedish_ci NULL,`preferred_color` varchar(6) CHARACTER SET latin1 COLLATE latin1_swedish_ci NOT NULL DEFAULT '000000',`icon_path` varchar(20) CHARACTER SET latin1 COLLATE latin1_swedish_ci NOT NULL,PRIMARY KEY (`order_status_info_id`) );